analysis::~analysis()
{
	if(computer.condition_index.size() != 0){
		for(int i = 0; (unsigned)i < computer.condition_index.size(); i++){
			free(computer.condition_index.at(computer.condition_index.size() - 1));
			computer.condition_index.pop_back();
		}
	}
	if(computer.J_address.size() != 0){
		for(int i = 0; (unsigned)i < computer.J_address.size(); i++){
			free(computer.J_address.at(computer.J_address.size() - 1));
			computer.J_address.pop_back();
		}
	}
	if(computer.Stack.size() != 0){
		for(int i = 0; (unsigned)i < computer.Stack.size(); i++){
			free(computer.Stack.at(computer.Stack.size() - 1));
			computer.Stack.pop_back();
		}
	}
	computer.Stack.clear();
	computer.Stack.~vector();
	computer.J_address.~vector();
	computer.condition_index.~vector();

	for(int i = 0; i < line + 1; i++) {
		if(operation[i])
			free(operation[i]);
	}
	free(operation);
	for(int i = 0; i < line + 1; i++) {
		if(information[i]){
			free(information[i]);
		}
	}
	free(information);
	for(int i = 0; i < line + 1; i++) {
		if(address[i]){
			free(address[i]);
		}
	}
	free(address);
	free(File_Data);

	if(check_decompile){
		mongoc_client_destroy (client_decompile);
		check_decompile = false;
	}
}

/*
File_ReadText함수
Assembly코드 파일을 불러와서 File_Data배열에 집어넣는다.
*/
void analysis::File_ReadText(char* filename){
	ST_FILE TEXT;
	char ch;
	char ch2;
	long len;
	long index = 0;

	TEXT.err = fopen_s(&TEXT.stream, filename, "r");

	if(TEXT.err != 0){
		printf("\nnot open File\n");
	}
	else{
		fseek(TEXT.stream, 0L, SEEK_END);
		len = ftell(TEXT.stream);
		fseek(TEXT.stream, 0L, 0);

		File_Data = (char *)malloc((sizeof(char) * len));

		while((EOF != (ch = fgetc(TEXT.stream))) || ((EOF == ch) && EOF != (ch2 = fgetc(TEXT.stream)))){
			//printf("%d\n", index);
			if(ch >= 0)
				File_Data[index++] = ch;
		}
		File_Data[index] = 0;
	}

	if(TEXT.stream){
		TEXT.err = fclose(TEXT.stream);

		if(TEXT.err == 0)
			printf("close File Success\n");
		else
			printf("close File Failed\n");
	}
}

/*
File_ReadText_Address함수
코드 영역에서 main주소를 가지고 와서 저장한다.
*/
void analysis::File_ReadText_Address(char* filename){
	ST_FILE TEXT;
	char ch;
	long len;
	long index = 0;

	TEXT.err = fopen_s(&TEXT.stream, filename, "r");

	if(TEXT.err != 0){
		printf("\nnot open File\n");
	}
	else{
		fseek(TEXT.stream, 0L, SEEK_END);
		len = ftell(TEXT.stream);
		fseek(TEXT.stream, 0L, 0);

		main_address = (char *)malloc((sizeof(char) * len));

		while(EOF != (ch = fgetc(TEXT.stream))){

			main_address[index++] = ch;
		}
		main_address[index] = 0;
		//COMPUTER.
	}

	if(TEXT.stream){
		TEXT.err = fclose(TEXT.stream);

		if(TEXT.err == 0)
			printf("close File Success\n");
		else
			printf("close File Failed\n");
	}
}

/*
save_separate_code함수
분리된 파일을 저장한다. 
처음 주소(인덱스), 끝 주소(인덱스)를 가지고 와서 해당 인덱스 사이의 address, operation, information을 저장한다.
*/
void analysis::save_separate_code(char** operation, char** address, char**information, int start_index, int end_index, int file_index){
	FILE* fp = NULL;
	stringstream converter;
	string file_name = ".\\separate_code\\";
	string file_name1 = "separate_code_";
	converter << file_index;
	string file_name2 = converter.str();
	string file_name3 = ".txt";

	file_name.append(&file_name1[0]);
	file_name.append(&file_name2[0]);
	file_name.append(&file_name3[0]);

	fopen_s(&fp, &file_name[0],"wt");

	if(NULL != fp){
		for(int i = start_index; i < end_index + 1; i++){
			fprintf(fp, "%s ", address[i]);
			fprintf(fp, "%s ", operation[i]);
			fprintf(fp, "%s\n", information[i]);
		}
		fclose(fp);
	}
}

