/*
set_information함수
Assembly코드가 저장된 File_Data를 형식대로 나누어 배열에 저장해주는 함수이다.
address, operation, information 3개의 형식으로 나누어 저장을 한다.
*/
void analysis::set_information(int line, char** operation, char** address, char** information){
	int row_address = 0;
	int col_address = 0;
	int row_operation = 0;
	int col_operation = 0;
	int row_information = 0;
	int col_information = 0;
	int spaceCount = 0;

	bool control_flag = false;
	bool set_complete = false;
	bool set_information = false;

	for(int i = 0; (unsigned)i < strlen(File_Data); i++){
		if(File_Data[i] == 48 && File_Data[i+1] == 56 && File_Data[i+2] == 57 && File_Data[i+3] == 67)
			printf("");
		if(set_complete == true){
			if(File_Data[i-1] == 10){
				row_address++;
				row_operation++;
				row_information++;
				col_address = 0;
				col_operation = 0;
				col_information = 0;

				address[row_address][col_address++] = File_Data[i];
				set_complete = false;
				control_flag = false;
				set_information = false;
			}
		}
		else{
			if(control_flag == false){
				address[row_address][col_address++] = File_Data[i];
				if(address[row_address][col_address-1] == 32){
					address[row_address][col_address-1] = 0;
					control_flag = true;

				}
			}
			else if(control_flag == true && set_information == false){
				operation[row_operation][col_operation++] = File_Data[i];
				if(operation[row_operation][col_operation-1] == 32){
					operation[row_operation][col_operation-1] = 0;
					//set_complete = true;
					set_information = true;
				}
				else if(operation[row_operation][col_operation-1] == 10){
					operation[row_operation][col_operation-1] = 0;
					information[row_information][0] = 0;
					set_information = true;
					set_complete = true;
				}
			}
			else{
				if(File_Data[i] == 29){
					information[row_information][col_information++] = ')';
					information[row_information][col_information] = 0;
					set_complete = true;
				}
				else
					information[row_information][col_information++] = File_Data[i];
				if(information[row_information][col_information-1] == 10){
					information[row_information][col_information-1] = 0;
					set_complete = true;
				}
			}
		}
	}
}

/*
separate_code함수
Assembly코드를 함수와 main영역으로 분리시킨다.
총 명령어 갯수 line을 받아와서 전체 명령어 만큼 돌면서 함수들과 main을 분리한다.
*/
void analysis::separate_code(int line, char** operation, char** address, char** information){
	int index = 0;
	int separate_start = 0;
	int separate_end = 0;
	int next_separate_start = 0;
	int file_index = 0;
	int main_addr = 0;
	int present_addr = 0;
	char* stop;

	bool separate_code_start = false;
	bool separate_code_end = false;
	bool find_new_function_start = false;
	bool has_next_address = false;
	main_addr = strtol(main_address, &stop, 16);
	while(1){

		if(index == 0){																															//첫 index = 0으로 시작.
			separate_code_start = true;
			separate_start = index;
		}
		else if(strcmp(operation[index + 1], "PUSH") == 0 && strcmp(information[index + 1], "EBP") == 0){
			if(strcmp(operation[index + 2], "MOV") == 0){
				if(strstr(information[index + 2], "EBP") && strstr(information[index + 2], "ESP")){
					separate_end = index;
					computer.file_end_index.push_back(separate_end);
					computer.file_start_index.push_back(separate_start);
					save_separate_code(operation, address, information, separate_start, separate_end, file_index);
					separate_count++;
					file_index++;
					separate_start = index + 1;
				}
			}
		}
		if(index + 1 == line){																																//마지막 파일.(끝이 됬을 때)
			if(separate_end != index){
				separate_start = separate_end + 1;
				separate_end = index;
				save_separate_code(operation, address, information, separate_start, separate_end, file_index);
				computer.file_end_index.push_back(separate_end);
				computer.file_start_index.push_back(separate_start);
				separate_count++;
			}
			break;
		}
		index++;
	}
}

