/* 
makeCfile함수
전부 디컴파일이 된 C언어파일들을 하나로 합쳐서 하나의 C언어 구조의 파일 형태로 만든다.
*/
void analysis::makeCfile()
{
	FILE* fp = NULL;
	FILE* completeC = NULL;
	char buf[2048];

	string fileName;
	string fileName1 = "void Function_";
	string fileName2;
	string fileName3 = "();";

	int mainFileIndex = 0;

	char** headerList;
	int headerListCount = 0;

	completeC = fopen("completeCFile.txt", "w");

	headerList = find_func(exeName, headerListCount);

	for(int i = 0; i < headerListCount; i++)
	{
		if(strcmp(headerList[i], "Unknown_header") != 0)
		{
			sprintf(buf, "#include <%s>\n", headerList[i]);
			fprintf(completeC, "%s", buf);
		}
	}
	fprintf(completeC, "\n");

	for(int fileIndex = 0; fileIndex < separate_count; fileIndex++)
	{
		fileName = "";
		stringstream converter;
		converter << fileIndex;
		fileName2 = converter.str();

		fileName.append(fileName1);
		fileName.append(fileName2);
		fileName.append(fileName3);

		fprintf(completeC, "%s\n", fileName.c_str());
	}
	fprintf(completeC, "\n");

	fp = fopen("Main_code.txt", "r");
	
	if(fp != NULL)
	{
		while(NULL != fgets(buf, 2048, fp))
		{
			fprintf(completeC, "%s", buf);
		}
		fprintf(completeC, "\n");
		fclose(fp);

		fileName1 = "separate_Function_code_";
		fileName3 = ".txt";

		for(int i = 0; i < separate_count; i++)
		{
			fileName = "";
			stringstream converter;
			converter << i;
			fileName2 = converter.str();

			fileName.append(fileName1);
			fileName.append(fileName2);
			fileName.append(fileName3);

			if( (fp = fopen(fileName.c_str(), "r")) == NULL)
			{
				mainFileIndex = i;
				continue;
			}
			fclose(fp);
		}

		for(int fileIndex = 0; fileIndex < separate_count; fileIndex++)
		{
			fileName = "";
			stringstream converter;
			converter << fileIndex;
			fileName2 = converter.str();

			fileName.append(fileName1);
			fileName.append(fileName2);
			fileName.append(fileName3);

			if( (fp = fopen(fileName.c_str(), "r")) == NULL)
			{
				continue;
			}

			while(NULL != fgets(buf, 2048, fp))
			{
				fprintf(completeC, "%s", buf);
			}

			fprintf(completeC, "\n");
			fclose(fp);
		}
	}
	fclose(completeC);
}

