/* Database Connection Funtion */
void analysis::start_db()
{
	connect_count = 5;
	client_decompile = NULL;
	bson_t *query = NULL;
	const bson_t *doc;
	mongoc_cursor_t *cursor = NULL;
	mongoc_collection_t *collection = NULL;
	bool connect_success = false;
	/*
		데이터베이스에 접속되어 있거나 5번의 접속 실패하 않았을 경우
		접속을 시도한다.
   */
	while(connect_success == false && connect_count > 0){
		/*
			반복문 시작하기 전 null값이 아닌 변수 메모리 할당 해제
	   */
		if(client_decompile)      mongoc_client_destroy(client_decompile);
		if(cursor)      mongoc_cursor_destroy(cursor);
		if(query)      bson_destroy(query);
		if(collection)   mongoc_collection_destroy(collection);
		/*
			초기화 및 데이터베이스 접속
			접속이 제대로 되었는지 확인하기 위해 find query 실행
	  */
		mongoc_init ();
		query = bson_new ();
		client_decompile = mongoc_client_new (server_address);         // connect ip address
		collection = mongoc_client_get_collection (client_decompile, database, "Total function");
		cursor = mongoc_collection_find (collection, MONGOC_QUERY_NONE, 0, 0, 0, query, NULL, NULL);
		/*
			접속이 제대로 되었으면 connect_success의 값 true, 실패하면 시도횟수 -1 후 처음부터 반복
			connect_success => 데이터베이스에 접속이 되었는지 확인하는 변수
	  */
		if(mongoc_cursor_next(cursor,&doc))   //Check connection
			connect_success = true;
		else{
			if(connect_count != 0)         //reconnection 5 times
				connect_count--;
		}
	}

	if(cursor)      mongoc_cursor_destroy(cursor);
	if(query)      bson_destroy (query);
	if(collection)   mongoc_collection_destroy (collection);
	if(connect_success == false) if(client_decompile) mongoc_client_destroy(client_decompile);
}

/*
	decompile_dynamic_size 함수
	데이터베이스에서 받아온 정보에서 원하는 field의 글자 수를 Count하여 리턴해주는 함수.
*/
int analysis::decompile_dynamic_size(char *str, char *query_var)
{
   int count = strlen(query_var);
   int findFirst = 0;
   int rtn_value=0;
   int total_value_cnt = 0;
   int print_str_cnt = 0;

   /*
		데이터베이스에서 받아온 정보에서 원하는 field를 찾는다.
		str => 데이터베이스에서 받아온 문장
		query_func_name => 원하는 field 이름
		ex) field1 : "value", field2 : "value" 
		str문장이 위와같은 식으로 되어있기 때문에  
		field명을 찾아준 후 total_value_cnt 값에 +5를 해준다.
   */
   while(rtn_value < count){
      if(findFirst == 0){
         if(str[total_value_cnt++] == query_var[rtn_value]){
            rtn_value++;
            findFirst = 1;
         }
      }
      else{
         if(str[total_value_cnt++] != query_var[rtn_value]){
            rtn_value = 0;
            findFirst = 0;
         }
         else{
            rtn_value++;
         }
      }
   }
   total_value_cnt += 5;
   /*
		원하는 field의 value값의 길이를 count하여 리턴해준다.
   */
   while(1){
      str[total_value_cnt++];
      print_str_cnt++; 
      if(str[total_value_cnt] == '\"')
         break;
   }

   return print_str_cnt+1;
}

/*
	decompile_value_strtok 함수
	데이터베이스에서 받아온 정보에서 field의 정보를 찾아 리턴해주는 함수.
*/
void analysis::decompile_value_strtok(char *str, char *query_var, char *print_str){
   int count = strlen(query_var);
   int findFirst = 0;
   int rtn_value=0;
   int total_value_cnt = 0;
   int print_str_cnt = 0;

   /*
		데이터베이스에서 받아온 정보에서 원하는 field를 찾는다.
		str => 데이터베이스에서 받아온 문장
		query_func_name => 원하는 field 이름
		ex) field1 : "value", field2 : "value" 
		str문장이 위와같은 식으로 되어있기 때문에  
		field명을 찾아준 후 total_value_cnt 값에 +5를 해준다.
   */
   while(rtn_value < count){
      if(findFirst == 0){
         if(str[total_value_cnt++] == query_var[rtn_value]){
            rtn_value++;
            findFirst = 1;
         }
      }
      else{
         if(str[total_value_cnt++] != query_var[rtn_value]){
            rtn_value = 0;
            findFirst = 0;
         }
         else{
            rtn_value++;
         }
      }
   }
   total_value_cnt += 5;

   /*
		원하는 field의 value값을 저장하여 리턴해준다.
   */
   while(1){

      print_str[print_str_cnt++] = str[total_value_cnt++];

      if(   str[total_value_cnt] == '\"')
         break;
   }
}

/* System Dll에 포함된 함수가 있는 header file 찾는 함수 */
char** analysis::find_func(char* filename,int& count)
{
   mongoc_collection_t *collection;
   mongoc_cursor_t *cursor;
   const bson_t *doc;         // do not adjust because of const.
   bson_t *pipeline;
   int headersize, func_count=0;
   char *str;
   char headername[20], headercmp[20];
   char **func_list;

   /*
		'PE imports' collection에서 분석한 파일의 이름인 함수들을
		Header name기준으로 정렬하여 find
	*/
   count = 0;
   collection = mongoc_client_get_collection (client_decompile, database, "PE imports");

   pipeline = BCON_NEW ("pipeline", "[",
      "{", "$match", "{", "Exe name",  BCON_UTF8 (filename), "}", "}",
      "{", "$sort", "{", "Header name",  BCON_INT32 (1), "}", "}"
      ,"]");

   cursor = mongoc_collection_aggregate (collection, MONGOC_QUERY_NONE, pipeline, NULL, NULL);
   /*
		header name 개수 확인
		중복, unknown제거
	*/
   while (mongoc_cursor_next (cursor, &doc)){   // 전체 개수 확인
      str = bson_as_json (doc, NULL);
      if(str){
         memset(headername,0,sizeof(char)*20);
         decompile_value_strtok(str,"Header name",headername);

         if(strcmp(headername,"Unknown header") && strcmp(headername,headercmp)){
            strcpy(headercmp,headername);
            count++;
         }
      }
      bson_free(str);
   }

   /*
		커서 초기화
	*/
   memset(headercmp,0,sizeof(char)*20);
   mongoc_cursor_destroy (cursor);
   cursor = mongoc_collection_aggregate (collection, MONGOC_QUERY_NONE, pipeline, NULL, NULL);   //커서 초기화

   func_list = (char**)malloc(sizeof(char*)*count);   //return할 배열 동적할당
   
	/*
		header name 목록을 중복, unknown 제거하여 저장
	*/
   while (mongoc_cursor_next (cursor, &doc)){
      str = bson_as_json (doc, NULL);
      if(str){
         memset(headername,0,sizeof(char)*20);
         decompile_value_strtok(str,"Header name",headername);

         if(strcmp(headername,"Unknown header") && strcmp(headername,headercmp)){   //unknown, 중복 피하기
            headersize = decompile_dynamic_size(str,"Header name");
            headersize++;
            func_list[func_count] = (char*)malloc(sizeof(char)*headersize);
            func_list[func_count];
            strcpy(func_list[func_count],headername);
            strcpy(headercmp,headername);
            func_count++;
         }
      }
      bson_free(str);
   }

   bson_destroy (pipeline);
   mongoc_cursor_destroy (cursor);
   mongoc_collection_destroy (collection);
   return func_list;
}

/* System Dll에 포함된 함수의 arguments, return type을 찾는 함수 */
char** analysis::find_value(char *query_info){

   mongoc_collection_t *collection;
   mongoc_cursor_t *cursor;
   const bson_t *doc;         // do not adjust because of const.
   bson_t *query = NULL, *update = NULL;
   char **parameter_info;

   char *str,*parameter_name = '\0', *para_cnt_type_char = '\0', *r_type = '\0';
   char *func_name = "\0",*dll_name = "\0", *func_tok = "\0";
   int func_cnt_type_int = 0, malloc_size = 0, token_cnt = 0,check_decompile = 0, para_length = 0, f_count = 0;

   /*
		함수에 Input 받은 parameter값 분리
		ex) kernel32.dll:_lcreat
		dll_name => kernel32.dll
		func_name => _lcreat
	*/
   func_tok = (char *)malloc(sizeof(char)*(strlen(query_info)+1));

   memset(func_tok,0,sizeof(char)*(strlen(query_info)+1));
   memset(dll_name,0,0);
   memset(func_name,0,0);

   strcpy(func_tok,query_info);
   dll_name = strtok(func_tok,":");
   func_name = strtok(NULL,":");

   /*
		데이터베이스에서 dll_name 값과 같은 collection에서 필요한 함수 find
	*/
   collection = mongoc_client_get_collection (client_decompile, database, dll_name);

   query = bson_new ();
   BSON_APPEND_UTF8(query,"_id",func_name);
   cursor = mongoc_collection_find (collection, MONGOC_QUERY_NONE, 0, 0, 0, query, NULL, NULL);

   /*
		함수를 찾으면 parameter_name, parameter_count, return_type을 저장
		데이터베이스에 원하는 함수가 없으면 para_cnt_type_char 값에 -1 저장
	*/
   if(mongoc_cursor_next(cursor,&doc)){

      str = bson_as_json (doc, NULL);
      if(str){
         malloc_size = decompile_dynamic_size(str,"parameter_name");
         parameter_name = (char *)malloc(sizeof(char)*malloc_size);
         memset(parameter_name,0,sizeof(char)*malloc_size);
         decompile_value_strtok(str,"parameter_name",parameter_name);   // print parameter
         para_length = INFORMATION_LENGTH_MAX;

         malloc_size = decompile_dynamic_size(str,"parameter_count"); 
         para_cnt_type_char = (char *)malloc(sizeof(char)*malloc_size);
         memset(para_cnt_type_char,0,sizeof(char)*malloc_size);
         decompile_value_strtok(str,"parameter_count",para_cnt_type_char);   // print parameter count
         para_cnt_type_int = atoi(para_cnt_type_char);

         malloc_size = decompile_dynamic_size(str,"return_type"); 
         r_type = (char *)malloc(sizeof(char)*malloc_size);
         memset(r_type,0,sizeof(char)*malloc_size);
         decompile_value_strtok(str,"return_type",r_type);   // print parameter count
      }
      bson_free (str);         // if you use bson_as_json, then you must free parameter.
   }
   else{
      /*
		parameter 값 저장
	*/
      para_cnt_type_int = -1;
   }

   /*
   parameter value return
   */
   para_cnt_type_int += 2;
   if(para_cnt_type_int != 0){
      parameter_info =(char**)malloc(sizeof(char*)*para_cnt_type_int);   // create dynamic array
      for(int i=0; i<para_cnt_type_int; i++){
         parameter_info[i] = (char*)malloc(sizeof(char)*(para_length+1));
         memset(parameter_info[i],0,sizeof(char)*(para_length+1));
      }
   }
   /*
   case1. parameter count is controled by user or user doesn't know parameter count (unknown)
   case2. function deosn't exist      : return -1
   case3. function have no parameter   : return 0
   */
   if(para_cnt_type_int == 0){ // case 1
      parameter_info =(char**)malloc(sizeof(char*)*3);   // create dynamic array
      for(int i=0; i<3; i++){
         parameter_info[i] = (char*)malloc(sizeof(char)*(para_length+1));
         memset(parameter_info[i],0,sizeof(char)*(para_length+1));
      }
      strcpy(parameter_info[0],"-2");
      strcpy(parameter_info[1],r_type);
      strcpy(parameter_info[2],parameter_name);
   }
   else if(para_cnt_type_int == 1){ // case 2
      strcpy(parameter_info[0],"-1");
   }
   else if(para_cnt_type_int == 2){ // case 3
      strcpy(parameter_info[0],"0");
      strcpy(parameter_info[1],r_type);
   }
   else{
      _itoa(para_cnt_type_int,parameter_info[0],10);
      strcpy(parameter_info[1],r_type);
      int j=0;
      int total_cnt = 0;

      for(int i=2; i<para_cnt_type_int; i++){            // save name in 2 dynamic dimensional array

         while(parameter_name[total_cnt] != ','){
            parameter_info[i][j] = parameter_name[total_cnt];
            j++;total_cnt++;
            if(parameter_name[total_cnt] == '\0')
               break;
         }
         parameter_info[i][j] = '\0';
         total_cnt++;j=0;
      }
   }

   /*
		할당 해제후 저장한 값 리턴
	*/
   bson_destroy (query);
   mongoc_cursor_destroy (cursor);
   mongoc_collection_destroy (collection);

   if(parameter_name)   
      free(parameter_name);
   if(para_cnt_type_char)   
      free(para_cnt_type_char);
   if(r_type)   
      free(r_type);
   if(func_tok)   
      free(func_tok);

   return parameter_info;
}